mapper.select:
select user_id
from user_info
where phone = #{phone,javaType=STRING}

select user_id as userId, name as name, role_model as roleModel
from user_info
where user_id = #{userId,jdbcType=INTEGER}

mapper.update:
update user_info set
    state = #{state}
where user_id = #{userId,javaType=INTEGER}

update user_info set
    name = #{name,jdbcType=VARCHAR}
where user_id = #{userId,jdbcType=INTEGER}
