mapper.update:
update user_relation
set child_name = #{childName}
where parent_id = #{parentId}
    and child_id = #{childId}

update user_relation
set parent_name = #{parentName}
where parent_id = #{parentId}
  and child_id = #{childId}

mapper.delete:
delete from user_relation
where parent_id = #{parentId,jdbcType=INTEGER}
    and child_id #{childId,jdbcType=INTEGER}

mapper.select:
select parent_name as parentName, child_name as childName
from user_relation
where parent_id = #{parentId,jdbcType=INTEGER}
    and child_id = #{childId,jdbcType=INTEGER}

select child_id as userId, child_name as name
from user_relation
where parent_id = #{parentId,jdbcType=INTEGER}

select child_id as userId, child_name as UserName
from user_relation
where child_id = #{childId,jdbcType=INTEGER}

select parent_id, parent_name
from user_relation
where child_id = #{childId,jdbcType=INTEGER}
